import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Image,Row,Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import Navbar from './nav';
import "./home.css";
class user extends Component {
  render() {
    // const {user}=this.props
    return (
      <div> 
         <Navbar/>
         <Row>
           <Col xs={6} md={4}>
            <Image  src={this.props.location.state.img} className="profile-pic"  /> 
            <h3 className="margineft:1em">{this.props.location.state.name}</h3> 
          </Col>
          <Col xs={6} md={4}>
         <h1>User Details</h1>

       <h3 class="text-secondary">User location={this.props.location.state.location}</h3> 
        <h3 class="text-secondary">Login as ={this.props.location.state.login}</h3> 
        <h3 className="text-primary">User followers={this.props.location.state.fllowers}</h3> 
        </Col>
        </Row>



      </div>
    );
  }
}

export default user;
