import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Image,Row,Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import Navbar from './nav';
import "./home.css";
class user extends Component {
  render() {
    // const {user}=this.props
    return (
      <div className="text-center "> 
         <Navbar/>
         <Row className="text-center">
          <Col xs={4} md={4}>
            <Image src={this.props.location.state.img} className=" pull-left vfloat-left text-center profile-pic "/>
            <br></br>
            <h3 className="text-center">Frank</h3> 
          </Col>

         <h1>User Details</h1>
        <h3 className="text-primary">User name={this.props.location.state.name}</h3> <br></br>
       <h3 class="text-secondary">User location={this.props.location.state.location}</h3> <br></br>
        <h3 class="text-secondary">Login as ={this.props.location.state.login}</h3> <br></br>
        <h3 className="text-primary">User followers={this.props.location.state.fllowers}</h3> <br></br>
        </Row>



      </div>
    );
  }
}

export default user;
