import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.css';
import Logo from "./logo.png";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./login.css";
import User from "./App";

class Api extends Component {
  state = {
    user: {}
  };



  render() {
    const {user}=this.state;
    return (
      <div className="App">
        <User user={user} />
      </div>
    );
  }
}

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: ""
    };
  }

  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = event => {
    event.preventDefault();
  }

  render() {
    return (
      <div className="Login " >
      <img class="center" src={Logo} style={{height:50}}/>
        <p className="text-center">Sign in to GitHub</p>
        <form onSubmit={this.handleSubmit} className="m-12">
          <FormGroup controlId="email" bsSize="small">
          Username or email address
            <FormControl
              autoFocus
              type="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="small">
            Password  <a href="/users/123" class="active" class="float-right">Forgot password?</a>
            <FormControl
              value={this.state.password}
              onChange={this.handleChange}
              type="password"
            />
          </FormGroup>


          <Button className="btn-success"
            block
            bsSize="large"
            disabled={!this.validateForm()}
            type="submit"
          >
            Login
          </Button>
        </form>
      </div>
    );
  }
}
